import { useState, useContext } from "react";
import NumberContext from "../contexts/numberContext";

function Play() {

const [stockedNumber, setStockedNumber] = useState();
const [gameStarting, setGameStarting] = useState(false);
const [inputValue, setInputValue] = useState();
// const [counter, setCounter] = useState(0);

const {numberToGuess} = useContext(NumberContext);

  const onNumber = (event) => {
    setInputValue(parseInt(event.target.value));
  }
  const onOk = () => { 
    setGameStarting(true);
    setStockedNumber(inputValue);
    // setCounter(counter+1)
  }

  const Enter = (e) => {
      if (e.key === "Enter") {
        onOk();
      }
  }

  const guessNumber = () => {
    if (!gameStarting) {
      return (<h1>Tapez votre num√©ro</h1>)
    }

  else if (gameStarting && stockedNumber < numberToGuess) {
    return (<p>Plus grand que {stockedNumber}</p>)
  }
  else if (gameStarting && stockedNumber > numberToGuess) {
    return (<p> Moins grand que {stockedNumber}</p>)
  }
}


  return <>
    <input 
      onKeyPress={Enter}
      onChange={onNumber} type='number'/>
    <button onClick={onOk}>Ok</button>

  </>
;}
export default Play;
