function Congratulation() {
  const {numberToGuess} = useContext(NumberContext);
  const {counter} = useContext(counterContext);
  const navigate = useNavigate();


  const replay = () => {
    navigate('/');
  }

   <img className='saw2' src={saw2} alt="saw" />  
